import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { addOne, removeOne } from "../store/slices/basketslices";
import { fetchProductsThunk } from "../store/slices/productslice";
import type { RootState, AppDispatch } from "../store/store";

export default function ProductList() {
  const dispatch = useDispatch<AppDispatch>();

  const productImages: Record<string, string> = {
    bread: "data:image/jpeg;base64,",
    milk: "data:image/jpeg;base64,",
    cheese: "data:image/jpeg;base64,",
    soup: "data:image/jpeg;base64,",
    butter: "data:image/jpeg;base64,",
  };

  const products = useSelector((s: RootState) => s.products.products);
  const basket = useSelector((s: RootState) => s.basket.items);

  console.log("Rendering ProductList", products, basket);

  useEffect(() => {
    dispatch(fetchProductsThunk());
  }, [dispatch]);
  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h3 className="text-xl font-bold mb-4 text-gray-700">Products</h3>

      <div className="space-y-4">
        {products.map((p) => (
          <div
            key={p.id}
            className="flex items-center justify-between border-b pb-4 hover:bg-gray-50 p-2 rounded transition"
          >
            <div className="flex items-center gap-4">
              <img
                src={productImages[p.name.toLowerCase()] || ""}
                alt={p.name}
                className="w-12 h-12 object-cover rounded-md shadow-sm"
              />

              <div>
                <div className="font-semibold text-gray-800">{p.name}</div>
                <div className="text-sm text-gray-500">
                  Â£{p.price.toFixed(2)}
                </div>
              </div>
            </div>

            {(basket[p.id] || 0) === 0 ? (
              <button
                onClick={() => dispatch(addOne(p.id))}
                className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-1.5 text-sm rounded-md shadow"
              >
                Add
              </button>
            ) : (
              <div className="flex items-center border rounded-md overflow-hidden shadow-sm">
                <button
                  onClick={() => dispatch(removeOne(p.id))}
                  className="px-3 py-1 bg-gray-100 hover:bg-gray-200 text-lg"
                >
                  -
                </button>

                <div className="px-4 py-1 text-sm font-medium">
                  {basket[p.id]}
                </div>

                <button
                  onClick={() => dispatch(addOne(p.id))}
                  className="px-3 py-1 bg-gray-100 hover:bg-gray-200 text-lg"
                >
                  +
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
